import styled from 'styled-components';
import {shade} from 'polished';

export const Title = styled.h1`
font-size: 48px;
color: #3A3A3A;
max-width: 450px;
line-height: 56px;
margin-top: 80px;
`;

export const Form = styled.form `
margin-top: 40px;
max-width: 700;
display: flex;

input {
  flex: 1;
  height: 70px;
  border: 0;
  border-radius: 5px 0 0 5px;

  &::placeholder {
    color: #a8a8b3;
  }
}

button {
  width: 210px;
  height: 70px;
  background: #0Ad361;
  border-radius: 5px 0 0 5px;
  border: 0;
  color: #FFF;
  font-weight: bold;
  transition: background-color 0.2s;


  &:hover {
    background: ${shade(0.2, '#0Ad361')};
  }
}

`
;
